    (
        #{item.createTime,jdbcType=DATE},
        #{item.wechatId,jdbcType=VARCHAR},
        #{item.guidanceCount,jdbcType=INTEGER},
        #{item.personalizedGuidanceCount,jdbcType=INTEGER}
    )

mapper.select:
SELECT 

FROM aikang.user_guidance_stat
WHERE wechat_id = #{wechatId,jdbcType=VARCHAR}
AND create_time = #{createTime,jdbcType=DATE}

mapper.insert:
INSERT INTO aikang.user_guidance_stat (
    create_time, wechat_id, guidance_count, personalized_guidance_count
) VALUES (
    #{createTime,jdbcType=DATE},
    #{wechatId,jdbcType=VARCHAR},
    #{guidanceCount,jdbcType=INTEGER},
    #{personalizedGuidanceCount,jdbcType=INTEGER}
)

mapper.update:
UPDATE aikang.user_guidance_stat
SET 
    guidance_count = #{guidanceCount,jdbcType=INTEGER},
    personalized_guidance_count = #{personalizedGuidanceCount,jdbcType=INTEGER}
WHERE wechat_id = #{wechatId,jdbcType=VARCHAR}
AND create_time = #{createTime,jdbcType=DATE}
